<div class="modal fade" id="store_details" role="dialog">
   <div class="modal-dialog">
      <!-- Modal content-->
      <div class="modal-content">
         <div class="modal-header">
            <button type="button" class="close" data-dismiss="modal">&times;</button>
            <h4 class="modal-title">Store Details</h4>
         </div>
         <div class="modal-body">
            <form>
               <div class="row">
                  <div class="col-md-3">
                     <p>Display Name</p>
                  </div>
                  <div class="col-md-9">
                     <div class="form-group">
                        <div class="input-group">
                           <input class="form-control" type="text">
                        </div>
                     </div>
                  </div>
               </div>
               <div class="row">
                  <div class="col-md-3">
                     <p>Business Description</p>
                  </div>
                  <div class="col-md-9">
                     <div class="form-group">
                        <div class="input-group">
                         <textarea class="form-control" placeholder="textarea" rows="4"></textarea>
                        </div>
                     </div>
                  </div>
               </div>
                <div class="row">
                  <div class="col-md-3">
                     <p>Web Preview</p>
                  </div>
                  <div class="col-md-9">
                     <div class="form-group">
                        
                        <p>This is how your display name will be visible to buyers on <i>BookChor</i></p>
                       
                     </div>
                  </div>
               </div>
                <hr>
               <div class="row">
                  <div class="col-md-6">
                     <p><b>RETURN AND REPLACEMENT POLICY</b></p>
                  </div>
               </div>
               <div class="row">
                  <div class="col-md-12">
                      <div class="input-group">
                      <textarea class="form-control" placeholder="Enter your return  policy" rows="4"></textarea>
                   </div>
                  </div>
               </div>
               <br>
               <div class="row">
                  <div class="col-md-12">
                      <div class="input-group">
                      <textarea class="form-control" placeholder="Enter your replacement  policy" rows="4"></textarea>
                   </div>
                  </div>
               </div>
            </form>
         </div>
         <div class="modal-footer">
             <button type="button" class="btn btn-primary">Cancel</button>
              <button type="button" class="btn btn-primary">Save</button>
         </div>
      </div>
   </div>
</div> 
 
